const question2Options = [
    { value: 'Շատ փոքր', image: 'path_to_very_small_image.jpg' },
    { value: 'Փոքր', image: 'path_to_small_image.jpg' },
    { value: 'Միջին', image: 'path_to_medium_image.jpg' },
    { value: 'Մեծ', image: 'path_to_large_image.jpg' },
    { value: 'Շատ մեծ', image: 'path_to_very_large_image.jpg' }
];

function createOptions(questionId, options, maxSelections = null) {
    const optionsContainer = document.querySelector(`#${questionId} .options`);
    options.forEach((option, index) => {
        const optionElement = document.createElement('div');
        optionElement.className = 'option';
        optionElement.innerHTML = `
            <img src="${option.image}" alt="${option.value}">
            <label>
                <input type="${maxSelections ? 'checkbox' : 'radio'}" name="${questionId}" value="${option.value}">
                ${option.value}
            </label>
        `;
        optionsContainer.appendChild(optionElement);

        // Add click event to the image
        const img = optionElement.querySelector('img');
        const input = optionElement.querySelector('input');
        img.addEventListener('click', () => {
            input.checked = !input.checked;
            if (maxSelections) {
                const checkedInputs = optionsContainer.querySelectorAll('input[type="checkbox"]:checked');
                if (checkedInputs.length > maxSelections) {
                    input.checked = false;
                }
            }
        });
    });

    // Add "Other" option
    const otherOption = document.createElement('div');
    otherOption.className = 'option other';
    otherOption.innerHTML = `
        <label>
            <input type="${maxSelections ? 'checkbox' : 'radio'}" name="${questionId}" value="other">
            Այլ
        </label>
        <input type="text" class="other-input" placeholder="Խնդրում ենք նշել">
    `;
    optionsContainer.appendChild(otherOption);

    const otherCheckbox = otherOption.querySelector('input[type="checkbox"], input[type="radio"]');
    const otherInput = otherOption.querySelector('.other-input');

    otherCheckbox.addEventListener('change', () => {
        otherInput.style.display = otherCheckbox.checked ? 'block' : 'none';
    });

    otherInput.addEventListener('input', () => {
        otherCheckbox.checked = otherInput.value.trim() !== '';
    });

    if (maxSelections) {
        const inputs = optionsContainer.querySelectorAll('input[type="checkbox"]');
        inputs.forEach(input => {
            input.addEventListener('change', () => {
                const checkedInputs = optionsContainer.querySelectorAll('input[type="checkbox"]:checked');
                if (checkedInputs.length > maxSelections) {
                    input.checked = false;
                }
            });
        });
    }
}

function initializeSurvey() {
    createOptions('question1', question1Options, 6);
    createOptions('question2', question2Options);

    const form = document.getElementById('surveyForm');
    const question1 = document.getElementById('question1');
    const question2 = document.getElementById('question2');

    form.addEventListener('submit', (e) => {
        e.preventDefault();
        const formData = new FormData(form);
        let question1Answers = formData.getAll('question1');
        let question2Answer = formData.get('question2');

        // Handle "Other" option for question1
        const otherInput1 = question1.querySelector('.other-input');
        if (question1Answers.includes('other') && otherInput1.value.trim() !== '') {
            question1Answers = question1Answers.filter(a => a !== 'other');
            question1Answers.push(otherInput1.value.trim());
        }

        // Handle "Other" option for question2
        const otherInput2 = question2.querySelector('.other-input');
        if (question2Answer === 'other' && otherInput2.value.trim() !== '') {
            question2Answer = otherInput2.value.trim();
        }

        if (question1Answers.length === 0) {
            alert('Խնդրում ենք պատասխանել առաջին հարցին:');
            return;
        }

        if (question2.style.display === 'none') {
            question1.style.display = 'none';
            question2.style.display = 'block';
            return;
        }

        if (!question2Answer) {
            alert('Խնդրում ենք պատասխանել երկրորդ հարցին:');
            return;
        }

        const data = {
            question1: question1Answers.join(', '),
            question2: question2Answer
        };

        submitFormJsonp(data);
    });
}

function submitFormJsonp(data) {
    const script = document.createElement('script');
    const callback = 'jsonpCallback_' + Date.now();

    window[callback] = function(response) {
        if (response.result === 'success') {
            alert('Շնորհակալություն հարցմանը մասնակցելու համար:');
            form.reset();
            question1.style.display = 'block';
            question2.style.display = 'none';
            // Hide all "Other" inputs
            document.querySelectorAll('.other-input').forEach(input => {
                input.style.display = 'none';
            });
        } else {
            alert('Տեղի է ունեցել սխալ: Խնդրում ենք փորձել կրկին:');
        }
        delete window[callback];
        document.body.removeChild(script);
    };

    const url = 'https://script.google.com/macros/s/AKfycbygV9Pm3155A3R0y2wW6gGM9JE4_a4Dze8LgPcS2uBW6U-oO_LFewB_irVbczpcbxS4/exec';
    const params = `?callback=${callback}&question1=${encodeURIComponent(data.question1)}&question2=${encodeURIComponent(data.question2)}`;
    script.src = url + params;
    document.body.appendChild(script);
}

// Call the initialization function when the DOM is fully loaded
document.addEventListener('DOMContentLoaded', initializeSurvey);
